
public class sumofnumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Sum of the numbers from 1 to 600");
		int sum = 0;
		for ( int i =1 ; i<=600 ; i++)
		{
			sum += i;
		}
		System.out.println("Sum of the numbers from 1 to 600 is: "+sum);

	}

}
